import AppUserCheckModal from "./modules/AppUserCheckModal";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import AppUserCheckAuditModal from "./modules/AppUserCheckAuditModal";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction } from "@api/manage";
import { filterObj } from "@/utils/util";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "AppUserCheckList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis,
    AppUserCheckModal: AppUserCheckModal,
    ProductNewBigImg: ProductNewBigImg,
    AppUserCheckAuditModal: AppUserCheckAuditModal
  },
  data: function data() {
    return {
      description: '实名认证审核',
      // 表头
      columns: [{
        title: '真实姓名',
        align: "center",
        dataIndex: 'realname'
      }, {
        title: '手机号码',
        align: 'center',
        dataIndex: 'telNumber'
      }, {
        title: '身份证号码',
        align: "center",
        dataIndex: 'idcard'
      }, {
        title: '手持身份证图片',
        align: "center",
        dataIndex: 'idcardimage',
        scopedSlots: {
          customRender: 'card'
        }
      }, {
        title: '身份证正面照片',
        align: "center",
        dataIndex: 'idcardfrontimage',
        scopedSlots: {
          customRender: 'card'
        }
      }, {
        title: '身份证反面照片',
        align: "center",
        dataIndex: 'idcardbackimage',
        scopedSlots: {
          customRender: 'card'
        }
      }, {
        title: '状态',
        align: "center",
        dataIndex: 'status',
        customRender: function customRender(text) {
          if (text === 1) {
            return '申请中';
          } else if (text === 2) {
            return '申请成功';
          } else if (text === 3) {
            return '申请失败';
          }
        }
      }, {
        title: '申请时间',
        align: "center",
        dataIndex: 'createtime'
      }, {
        title: '审核时间',
        align: "center",
        dataIndex: 'checktime'
      }, {
        title: '现居地址',
        align: "center",
        dataIndex: 'address',
        scopedSlots: {
          customRender: 'templateAddress'
        }
      }, {
        title: '审核失败原因',
        align: "center",
        dataIndex: 'failreason',
        scopedSlots: {
          customRender: 'templateFailreason'
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: "/user/appusercheck/findCheckList",
        delete: "/user/appUserCheck/delete",
        auditTrue: '/user/appusercheck/auditTrue'
      }
    };
  },
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
        this.$refs.bigImg.width = 600;
      }
    },
    //  申请通过
    okCheck: function okCheck(userid) {
      var _this = this;

      var formData = {
        id: userid
      };
      getAction(this.url.auditTrue, formData).then(function (res) {
        if (res.success) {
          _this.$message.success(res.message);

          _this.loadData();
        } else {
          _this.$message.success(res.message);
        }
      }).catch(function (err) {
        _this.$message.success(err.message);
      });
    },
    //  审核拒绝
    falseCheck: function falseCheck(userid) {
      this.$refs.auditModel.open(userid);
    },
    getQueryParams: function getQueryParams() {
      //获取查询条件
      var sqp = {};

      if (this.superQueryParams) {
        sqp['superQueryParams'] = encodeURI(this.superQueryParams);
      }

      var param = Object.assign(sqp, this.queryParam, this.isorter, this.filters);
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.checkType = param.checkType ? param.checkType : '';
      param.realName = param.realName ? param.realName : '';
      return filterObj(param);
    }
  }
};
