import { postAction } from "@/api/manage";
export default {
  name: "ShareId",
  data: function data() {
    return {
      visible: false,
      userid: null,
      code: null,
      confirmLoading: false,
      isVyunShare: null,
      hide: false,
      url: {
        setGoodNumber: "/system/appuserinfo/setGoodNumber" // 设置靓号

      }
    };
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.userid = record.userid;
      this.isVyunShare = 1;

      if (record.ambassador == 0) {
        this.hide = false;
      } else {
        this.hide = true;
      }
    },
    close: function close() {
      this.$emit("close");
      this.userid = null;
      this.visible = false;
      this.code = null;
      this.isVyunShare = null;
    },
    handleOk: function handleOk() {
      var that = this;

      if (this.code == undefined || this.code == "") {
        this.$message.warning("数值不能为空！");
        return;
      }

      var arr = "?userid=".concat(this.userid, "&code=").concat(this.code);
      console.log(this.code);
      postAction(this.url.setGoodNumber + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
        that.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
