import ProfitRateModals from "./modules/ProfitRateModals";
import { postAction, deleteAction, putAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "ProviderList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis,
    ProfitRateModals: ProfitRateModals
  },
  data: function data() {
    return {
      description: "服务商分润比例列表",
      fenxiao: true,
      vipsorter: {
        order: "desc"
      },
      // 表头
      columns: [{
        title: "角色",
        align: "center",
        dataIndex: "role",
        customRender: function customRender(text) {
          if (text == "1") {
            return "VIP";
          } else if (text == "2") {
            return "创客达人";
          } else if (text == "3") {
            return "大使服务商";
          }
        }
      }, {
        title: "主播获得打赏比例",
        align: "center",
        dataIndex: "ratio",
        scopedSlots: {
          customRender: "rebate"
        }
      }, {
        title: "达标金额",
        align: "center",
        dataIndex: "qualifiedMoney"
      }, // {
      //   title: "创建时间",
      //   align: "center",
      //   dataIndex: "createTime"
      // },
      {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/system/service/profit/list",
        delete: "/system/service/profit/delete"
      }
    };
  },
  methods: {
    handleDelete: function handleDelete(record) {
      console.log(record);
      var id = record.id;

      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }

      var that = this;
      deleteAction(that.url.delete, {
        id: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
