import { postAction } from "@/api/manage";
export default {
  name: "AppUserInfoChangeModel",
  data: function data() {
    return {
      title: '修改',
      id: '',
      visible: false,
      type: '',
      typevulue: '',
      isShareProfit: 0,
      //是否分润
      confirmLoading: false,
      defaultIsVip: false,
      url: {
        setMoney: '/system/appuserinfo/setMoney',
        //修改V币
        setBalance: '/system/appuserinfo/setBalance',
        //修改V钻
        setCredits: '/system/appuserinfo/setCredits',
        //修改大V值
        setVip: '/system/appuserinfo/setVip',
        setMarker: "/system/appuserinfo//set-marker" //修改标识

      }
    };
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.id = record.userid;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.id = '';
      this.type = '';
      this.typevulue = null;
    },
    handleOk: function handleOk() {
      var that = this;
      console.log(this.typevulue);

      if ((this.typevulue == undefined || this.typevulue == '') && this.typevulue !== 0) {
        this.$message.warning('数值不能为空！');
        return;
      }

      var arr = '';
      var thisurl = '';
      that.confirmLoading = true;

      if (this.type === 'setMoney') {
        arr = "?userid=".concat(this.id, "&money=").concat(this.typevulue);
        thisurl = this.url.setMoney;
      } else if (this.type === 'setBalance') {
        arr = "?userid=".concat(this.id, "&balance=").concat(this.typevulue);
        thisurl = this.url.setBalance;
      } else if (this.type === 'setCredits') {
        arr = "?userid=".concat(this.id, "&credits=").concat(this.typevulue);
        thisurl = this.url.setCredits;
      } else if (this.type === 'setMarker') {
        arr = "?userId=".concat(this.id, "&marker=").concat(this.typevulue);
        thisurl = this.url.setMarker;
      }

      console.log(arr);
      postAction(thisurl + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit('ok');
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
        that.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
