import Vue from 'vue';
import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import JCodeEditor from "@/components/jeecg/JCodeEditor";
import JEditor from "@/components/jeecg/JEditor";
import AvatarModal from "@/views/account/settings/AvatarModal";
import ProductAddAlbumModal from "./ProductAddAlbumModal";
import * as qiniu from 'qiniu-js';
export default {
  name: "ProductNewModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 17
        }
      },
      labelColk: {
        xs: {
          span: 24
        },
        sm: {
          span: 2
        }
      },
      wrapperColk: {
        xs: {
          span: 24
        },
        sm: {
          span: 21
        }
      },
      loading: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      coverimg: '',
      canimg: '',
      percent: '',
      refusereason: '',
      disableSubmit: false,
      albumList: [],
      content: '',
      headers: {},
      selectList: [],
      albumName: '',
      //专辑名称
      authorName: '',
      //作者名称
      validatorRules: {},
      url: {
        add: "/product/productNew/add",
        edit: "/product/productNew/edit",
        getbyId: '/product/productNew/queryById',
        selectNewList: '/product/productCategory/queryFirstSelect',
        //分类选择
        albumlist: '/user/album/findAlbumSelect',
        // 专辑
        findList: '/product/productsource/findListByProductId',
        // 课程资源详情
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken'
      }
    };
  },
  components: {
    JCodeEditor: JCodeEditor,
    JEditor: JEditor,
    AvatarModal: AvatarModal,
    ProductAddAlbumModal: ProductAddAlbumModal
  },
  created: function created() {
    var _this = this;

    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    }; // this.getAblum()

    getAction(this.url.selectNewList).then(function (res) {
      if (res.success) {
        _this.selectList = res.result;
      } else {
        _this.$message.warning(res.message);
      }
    });
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    add: function add() {
      this.coverimg = '';
      this.refusereason = '';
      this.content = '';
      this.edit({});
    },
    edit: function edit(record) {
      var _this2 = this;

      console.log(record.id);
      this.coverimg = record.coverimg;
      this.canimg = record.coverimg;
      this.form.resetFields(); // this.getAblum()

      if (record.id) {
        this.getNewModel(record.id, record.type);

        if (record.type === '1' || record.type === '3') {
          this.getContentModel(record.id);
        }
      } else {
        this.model = Object.assign({}, record);
        this.visible = true;
        this.$nextTick(function () {
          _this2.form.setFieldsValue(pick(_this2.model, 'title', 'introduction', 'fistcategory', 'coverimg', 'price'));
        });
      }
    },
    //获取课程专辑
    getAblum: function getAblum() {
      var _this3 = this;

      httpAction(this.url.albumlist, {}, 'POST').then(function (res) {
        if (res.success) {
          _this3.albumList = res.result;
        } else {
          _this3.$message.warning(res.message);
        }
      });
    },
    // 根据id获取具体数据
    getNewModel: function getNewModel(id, type) {
      var _this4 = this;

      var that = this;
      getAction(this.url.getbyId, {
        id: id
      }).then(function (res) {
        if (res.success) {
          var record = res.result.product;
          _this4.refusereason = record.refusereason;
          _this4.albumName = res.result.albumName;
          _this4.authorName = res.result.authorName;

          if (type !== '1' && type !== '3') {
            _this4.content = record.content;
          }

          _this4.model = Object.assign({}, record);
          _this4.visible = true;

          _this4.$nextTick(function () {
            _this4.form.setFieldsValue(pick(_this4.model, 'title', 'introduction', 'fistcategory', 'coverimg', 'price'));
          });
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    //获取课程详情
    getContentModel: function getContentModel(id) {
      var _this5 = this;

      var html = '';
      var that = this;
      getAction(this.url.findList, {
        productId: id
      }).then(function (res) {
        if (res.success) {
          var record = res.result;
          html += '<div>';
          record.forEach(function (item) {
            if (item.title && item.title !== '') {
              html += "<strong style=\"margin-left: 100px;\">".concat(item.title, "</strong>");
            }

            switch (item.type) {
              case 'img':
                html += "<p><img src=\"".concat(item.url, "\" style=\"width:300px;\" /></p>");
                break;

              case 'text':
                html += "<p style=\"text-indent: 2em;\">".concat(item.url, "</p>");
                break;

              case 'video':
                if (item.timeurl && item.timeurl !== '') {
                  html += "<a href=\"".concat(item.url, "\" target=\"_blank\" style=\"display:block;width:300px;height:150px;background-image:url(").concat(item.timeurl, ");background-size: 100% auto;object-fit:cover;position:relative;\"><img src=\"").concat(require('@assets/play.png'), "\" alt=\"\" style=\"position: absolute;width: 50px;height: 50px;top: 50%;left: 50%;transform: translate(-50%,-50%);\"></a>");
                } else {
                  html += "<p><video controls=\"controls\" width=\"300\" height=\"150\"><source src=\"".concat(item.url, "\" /></video></p>");
                }

                break;

              case 'audio':
                html += "<p><audio src=\"".concat(item.url, "\" controls=\"controls\"></audio></p>");
                break;

              default:
                break;
            }
          });
          html += '</div>';
          _this5.content = html;
          console.log(record);
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.coverimg = '';
      this.disableSubmit = false;
      this.content = '';
    },
    handleOk: function handleOk() {
      var _this6 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this6.model.id) {
            httpurl += _this6.url.add;
            method = 'post';
          } else {
            httpurl += _this6.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this6.model, values);
          formData.coverimg = _this6.coverimg;
          formData.content = _this6.content; //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    customRequest: function customRequest(data) {
      var _this7 = this;

      var suffix = "." + data.file.name.replace(/^.+\./, ''); //请求token

      httpAction(this.url.qiniuToken, {}, 'post').then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this7.url.fileUpload;
          var config = {
            useCdnDomain: true,
            region: null
          };
          var putExtra = {
            fname: '',
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this7.loading = true;
              _this7.percent = res.total.percent.toFixed(2);
              _this7.coverimg = '';
            },
            error: function error() {
              _this7.$message.error('上传失败');

              _this7.loading = false;
              _this7.coverimg = _this7.canimg;
            },
            complete: function complete(response) {
              _this7.coverimg = "".concat(domain, "/").concat(response.key);
              _this7.loading = false;
            }
          });
        }
      });
    },
    addAlbum: function addAlbum() {
      this.$refs.addAblumModal.add();
      this.$refs.addAblumModal.visible = true;
    },
    modalFormOk: function modalFormOk() {
      var _this8 = this;

      httpAction(this.url.albumList, {}, 'POST').then(function (res) {
        if (res.success) {
          _this8.albumList = res.result;
        } else {
          _this8.$message.warning(res.message);
        }
      });
    }
  }
};
