import { putAction, postAction, httpAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "awardCard",
  data: function data() {
    return {
      icon: "",
      canimg: "",
      // 课程封面副
      title: "操作",
      percent: "",
      // 图片上传进度
      loading: false,
      visible: false,
      model: {},
      parentid: undefined,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/question/category/add",
        edit: "/system/question/category/update",
        //修改问题分类
        fileUpload: window._CONFIG["domianQiNiu"],
        // 七牛云地址
        qiniuToken: "/qiniuUpToken" // 请求七牛云上传的token

      }
    };
  },
  created: function created() {},
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    add: function add() {
      this.icon = "";
      this.percent = "";
      this.confirmLoading = false;
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.icon = record.icon;
      this.canimg = record.icon;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;

      if (record.parentId === 0) {
        this.model.parentId = "";
      }

      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "name", "icon", "parentId", "sort"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.percent = "";
      this.icon = "";
      this.visible = false;
    },
    // 类型选择
    typeSelectHandle: function typeSelectHandle(value) {
      this.type = value;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var formData = Object.assign(_this2.model, values);
          formData.parentId = formData.parentId != 0 ? formData.parentId + "" : undefined + "";
          formData.icon = _this2.icon;

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            var arr = "?icon=".concat(formData.icon, "&name=").concat(formData.name, "&parentId=").concat(formData.parentId, "&sort=").concat(formData.sort);
            arr = encodeURI(arr);
            postAction(httpurl + arr).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
              } else {
                that.$message.warning(res.message);
              }
            }).finally(function () {
              that.confirmLoading = false;
              that.close();
            });
          } else {
            httpurl += _this2.url.edit;

            var _arr = "?categoryId=".concat(_this2.model.id, "&icon=").concat(formData.icon, "&name=").concat(formData.name, "&parentId=").concat(formData.parentId, "&sort=").concat(formData.sort);

            _arr = encodeURI(_arr);
            putAction(httpurl + _arr).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
              } else {
                that.$message.warning(res.message);
              }
            }).finally(function () {
              that.confirmLoading = false;
              that.close();
            });
          }
        }
      });
    },
    // 图片自定义上传
    imgCustomRequest: function imgCustomRequest(data) {
      var _this3 = this;

      // 开始转圈圈，上传中
      this.loading = true; // 获取文件的后缀名

      var suffix = "." + data.file.name.replace(/^.+\./, ""); //请求token

      httpAction(this.url.qiniuToken, {}, "post").then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this3.url.fileUpload;
          var config = {
            useCdnDomain: true,
            // 域名加速
            region: null // 不指定具体区域，自动识别

          };
          var putExtra = {
            fname: "",
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this3.percent = res.total.percent.toFixed(2);
              _this3.icon = "";
            },
            error: function error() {
              _this3.$message.error("上传失败");

              _this3.loading = false;
              _this3.icon = _this3.canimg;
            },
            complete: function complete(response) {
              var allUrl = "".concat(domain, "/").concat(response.key); // 开始转圈圈，上传中

              _this3.icon = allUrl;
              _this3.loading = false;
            }
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
