import { postAction, putAction } from "@/api/manage";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import ForbiddenRoom from "./modules/ForbiddenRoom";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "LiveRoom",
  mixins: [JeecgListMixin],
  components: {
    ProductNewBigImg: ProductNewBigImg,
    ForbiddenRoom: ForbiddenRoom,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: "直播房间记录管理",
      columns: [{
        title: "用户名称",
        align: "center",
        dataIndex: "nickName"
      }, {
        title: "房间号",
        align: "center",
        dataIndex: "code"
      }, {
        title: "推流地址",
        align: "center",
        dataIndex: "coverImg",
        scopedSlots: {
          customRender: "templateStream"
        }
      }, {
        title: "展示图",
        align: "center",
        dataIndex: "showImg",
        scopedSlots: {
          customRender: "header"
        }
      }, {
        title: "直播标题",
        align: "center",
        dataIndex: "title"
      }, {
        title: "屏蔽词",
        align: "center",
        dataIndex: "blockWord"
      }, {
        title: "开通商户",
        align: "center",
        dataIndex: "merchant",
        customRender: function customRender(text) {
          if (text === 0) {
            return "否";
          } else if (text === 1) {
            return "是";
          }
        }
      }, {
        title: "直播间状态",
        align: "center",
        dataIndex: "state",
        customRender: function customRender(text) {
          if (text === 0) {
            return "关闭";
          } else if (text === 1) {
            return "开启";
          } else if (text === 2) {
            return "封禁";
          }
        }
      }, {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/system/live/room/list",
        update: "/system/live/room/updateStatus",
        brea: "/system/live/room/unseal",
        Merchant: "/system/live/room/openMerchant",
        closedid: "/system/live/room/closed"
      }
    };
  },
  methods: {
    //  封禁直播间
    handleBreak: function handleBreak(record) {
      console.log(record);
      var that = this;
      var roomId = record.id;
      var state = record.state;

      if (state === 2) {
        var en = 0;
        var arr = "?state=".concat(en, "&roomId=").concat(roomId, "&code=").concat(record.code);
      } else {
        var en = 0;
        var arr = "?state=".concat(en, "&roomId=").concat(roomId, "&code=").concat(record.code);
      }

      putAction(this.url.brea + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit("ok");
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
      });
    },
    handleMerchant: function handleMerchant(record) {
      var that = this;
      var chartId = record.id;
      var fromDarr = "?id=".concat(chartId);
      postAction(this.url.Merchant + fromDarr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit("ok");
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
      });
    },
    handleClose: function handleClose(record) {
      var that = this;
      var chartId = record.id;
      var fromDarr = "?id=".concat(chartId, "&code=").concat(record.code);
      postAction(this.url.closedid + fromDarr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit("ok");
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
      });
    },
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    handleBanRoom: function handleBanRoom(record) {
      this.$refs.modalForm.open(record);
      this.$refs.modalForm.title = "封禁房间";
    },
    click: function click(record) {
      console.log(record);
      var that = this;

      if (record.state != 1) {
        alert("当前直播已关闭，请查看其他直播吧~");
      } else {
        //       window.open(
        //   "http://192.168.110.29:5501/liveplayer/lxPc.html?flv=" + record.streamAddrUrl
        // );
        window.open("http://vyun.dujialive.com/liveplayer/lxPc.html?flv=" + record.streamAddrUrl);
      } // window.location.href = "http://127.0.0.1:5501/liveplayer/lx1.html?flv="+record.streamAddrUrl

    }
  }
};
