import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { getAction } from "@/api/manage";
export default {
  name: "LiveWheat",
  mixins: [JeecgListMixin],
  data: function data() {
    return {
      description: "直播连麦列表",
      columns: [{
        title: "主方名称",
        align: "center",
        dataIndex: "mainName"
      }, {
        title: "主方币值",
        align: "center",
        dataIndex: "mainMoney"
      }, {
        title: "对方名称",
        align: "center",
        dataIndex: "otherName"
      }, {
        title: "对方币值",
        align: "center",
        dataIndex: "otherMoney"
      }, {
        title: "类型",
        align: "center",
        dataIndex: "type",
        customRender: function customRender(text) {
          if (text == 1) {
            return "连麦";
          } else if (text == 2) {
            return "PK";
          }
        }
      }, {
        title: "状态",
        align: "center",
        dataIndex: "state",
        customRender: function customRender(text) {
          if (text == 1) {
            return "主方赢";
          } else if (text == 2) {
            return "平局";
          } else if (text == 3) {
            return "对方赢";
          }
        }
      }, {
        title: "创建时间",
        align: "center",
        dataIndex: "createTime",
        scopedSlots: {
          customRender: "createTime"
        }
      }],
      url: {
        list: "/system/live/connect/mic/list"
      }
    };
  },
  methods: {
    loadData: function loadData(arg) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      } //加载数据 若传入参数1则加载第一页的内容


      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //查询条件

      this.loading = true;
      getAction(this.url.list, params).then(function (res) {
        if (res.success) {
          console.log(res);
          _this.dataSource = res.result.pageList.records;
          _this.ipagination.total = res.result.pageList.total;
          _this.total = res.result.pageList.total;
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    }
  }
};
