import { putAction, postAction, httpAction, getAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "ProductCategoryModal",
  data: function data() {
    return {
      imageUrl: "",
      // canimg:"",
      // upFatherId:1000,
      title: "操作",
      visible: false,
      show: false,
      model: {},
      percent: "",
      loading: false,
      fatherid: undefined,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/product/productCategory/add",
        edit: "/product/productCategory/edit",
        fileUpload: window._CONFIG["domianQiNiu"],
        // 七牛云地址
        qiniuToken: "/qiniuUpToken" // 请求七牛云上传的token

      }
    };
  },
  created: function created() {},
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    //父级节点选项重置
    fatherIdReset: function fatherIdReset() {
      var _this = this;

      this.model.fatherId = this.fatherid;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "fatherId"));
      });
    },
    add: function add() {
      this.imageUrl = "";
      this.percent = "";
      this.confirmLoading = false;
      this.edit({});
    },
    typeSelectHandle: function typeSelectHandle(value) {
      if (value) {
        this.show = true;
      } else {
        this.show = false;
      }
    },
    edit: function edit(record) {
      var _this2 = this;

      this.show = false;
      this.imageUrl = record.imageUrl; // this.canimg = record.imageUrl;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.fatherid = record.fatherId == 0 ? record.fatherId : undefined;
      this.visible = true;

      if (record.fatherId === 0) {
        this.model.fatherId = "";
      }

      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, "name", "icon", "fatherId", "level", "sorts", "imageUrl"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.imageUrl = "";
      this.percent = "";
      this.visible = false;
    },
    // 图片自定义上传
    imgCustomRequest: function imgCustomRequest(data) {
      var _this3 = this;

      // 开始转圈圈，上传中
      this.loading = true; // 获取文件的后缀名

      var suffix = "." + data.file.name.replace(/^.+\./, ""); //请求token

      httpAction(this.url.qiniuToken, {}, "post").then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this3.url.fileUpload;
          var config = {
            useCdnDomain: true,
            // 域名加速
            region: null // 不指定具体区域，自动识别

          };
          var putExtra = {
            fname: "",
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this3.percent = res.total.percent.toFixed(2);
              _this3.imageUrl = "";
            },
            error: function error() {
              _this3.$message.error("上传失败");

              _this3.loading = false; // this.imageUrl = this.canimg;
            },
            complete: function complete(response) {
              var allUrl = "".concat(domain, "/").concat(response.key); // 开始转圈圈，上传中

              _this3.imageUrl = allUrl;
              _this3.loading = false;
            }
          });
        }
      });
    },
    handleOk: function handleOk() {
      var _this4 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var method = "";

          if (!_this4.model.id) {
            httpurl += _this4.url.add;
            method = "post";
          } else {
            httpurl += _this4.url.edit;
            method = "put";
          }

          var formData = Object.assign(_this4.model, values);
          formData.imageUrl = _this4.imageUrl;
          formData.fatherId = formData.fatherId != 0 ? formData.fatherId : undefined;
          formData.level = !formData.fatherId ? 1 : 2; //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit("ok");
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
