import { postAction, deleteAction, getAction } from "@/api/manage";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
import ExpressModals from "./modules/ExpressModals";
export default {
  name: "ExpressCompany",
  mixins: [JeecgListMixin],
  components: {
    ExpressModals: ExpressModals,
    ProductNewBigImg: ProductNewBigImg,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: "快递公司",
      selectList: [],
      columns: [{
        title: "公司名称",
        align: "center",
        dataIndex: "name"
      }, {
        title: "手机号",
        align: "center",
        dataIndex: "phone"
      }, {
        title: "公司标识",
        align: "center",
        dataIndex: "icon",
        scopedSlots: {
          customRender: "header"
        }
      }, {
        title: "是否禁用",
        align: "center",
        dataIndex: "enable",
        customRender: function customRender(text) {
          if (text == 0) {
            return "是";
          } else if (text == 1) {
            return "否";
          }
        }
      }, {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/system/mall/company/list",
        delete: "/system/mall/company/delete",
        findCompanyName: "/system/mall/company/findCompanyName"
      }
    };
  },
  created: function created() {
    var _this = this;

    getAction(this.url.findCompanyName).then(function (res) {
      console.log(res);

      if (res.success) {
        _this.selectList = res.result;
      } else {
        _this.$message.warning(res.message);
      }
    });
  },
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    handleDelete: function handleDelete(record) {
      var id = record.id;

      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }

      var that = this;
      deleteAction(that.url.delete, {
        cId: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    handleMembersList: function handleMembersList(record) {
      this.$refs.modalForm.open(record);
    }
  }
};
