import { getAction } from "@/api/manage";
export default {
  name: "BigTeamLookModal",
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      teamId: null,
      loading: false,
      dataSource: [],
      // 表头
      columns: [{
        title: '昵称',
        align: "center",
        dataIndex: 'nickName'
      }, {
        title: '手机号',
        align: "center",
        dataIndex: 'phone'
      }, {
        title: 'V云号',
        align: "center",
        dataIndex: 'code'
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: '/system/appuserinfo/findBigTeamUserList',
        delete: '/system/appuserinfo/delTeamByUserId'
      }
    };
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.teamId = record.id;
      this.loadData();
    },
    loadData: function loadData() {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      }

      var params = {
        teamId: this.teamId
      }; //查询条件

      this.loading = true;
      getAction(this.url.list, params).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result;
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
      this.visible = false;
    },
    //  删除成员
    lookDelete: function lookDelete(userId) {
      var _this2 = this;

      var param = {
        teamId: this.teamId,
        userId: userId
      };
      var that = this;
      that.confirmLoading = true;
      getAction(this.url.delete, param).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;

        _this2.loadData();
      });
    }
  }
};
