import pick from 'lodash.pick';
import moment from "moment";
import ProductNewBigImg from "../../product/modules/ProductNewBigImg";
export default {
  name: "FeedbackModal",
  components: {
    ProductNewBigImg: ProductNewBigImg
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      coverimg: [],
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {}
    };
  },
  created: function created() {},
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text, width) {
      if (text) {
        this.$refs.bigImg.make(text, width);
      }
    },
    edit: function edit(record) {
      var _this = this;

      var thiscover = record.bak ? record.bak.split(';') : [];

      if (thiscover.length > 0 && thiscover[0].indexOf(',') > -1) {
        this.coverimg = thiscover[0].split(',');
      } else {
        this.coverimg = thiscover;
      }

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'phone', 'name', 'content', 'bak'));

        _this.form.setFieldsValue({
          createTime: _this.model.createTime ? moment(_this.model.createTime) : null
        });
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
