import { getAction } from "@/api/manage";
export default {
  name: "ShareBonusModal",
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      form: this.$form.createForm(this),
      validatorRules: {
        money: {
          rules: [{
            required: true,
            message: '请输入分红金额!'
          }, {
            validator: this.validatorDictMoney
          }]
        }
      },
      url: {
        list: '/system/appuserinfo/particMoney'
      }
    };
  },
  methods: {
    validatorDictMoney: function validatorDictMoney(rule, value, callback) {
      if (value < 0) {
        callback('分红金额必须大于0');
      } else {
        callback();
      }
    },
    open: function open() {
      this.visible = true;
      this.form.resetFields();
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this;
      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var formData = Object.assign(_this.model, values);
          getAction(_this.url.list, formData).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');

              _this.close();
            } else {
              that.$message.warning(res.message);
            }
          }).catch(function (err) {
            _this.$message.warning(err.message);
          }).finally(function () {
            that.confirmLoading = false;
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
