import { postAction, getAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
import UserUnderList from "./modules/UserUnderList";
export default {
  name: "AmbassadorUnder",
  mixins: [JeecgListMixin],
  components: {
    UserUnderList: UserUnderList,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: "大使伞下大使关系",
      // queryParam: {
      //   rate: this.total
      // },
      columns: [{
        title: "V云号",
        align: "center",
        dataIndex: "code"
      }, {
        title: "用户昵称",
        align: "center",
        dataIndex: "nickName"
      }, {
        title: "伞下用户V云号",
        align: "center",
        dataIndex: "umbrellaCode"
      }, {
        title: "伞下用户昵称",
        align: "center",
        dataIndex: "umbrellaName"
      } // {
      //   title: "操作",
      //   align: "center",
      //   dataIndex: "inviteId",
      //   width: 180,
      //   scopedSlots: { customRender: "action" }
      // }
      ],
      url: {
        list: "/system/umbrella/ambassador/list"
      }
    };
  },
  methods: {
    searchQuery: function searchQuery() {
      this.loadData(1);
    },
    searchReset: function searchReset() {
      this.queryParam = this.param || {};
      this.loadData(1);
    },
    loadData: function loadData(arg) {
      var _this = this;

      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //查询条件

      this.loading = true;
      getAction(this.url.list, params).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result.records;
          _this.ipagination.total = res.result.total;
          console.log(res);
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    }
  }
};
