import { getAction } from "@/api/manage";
export default {
  name: "WithdrawAuditModal",
  data: function data() {
    return {
      id: '',
      visible: false,
      refusereason: '',
      confirmLoading: false,
      auditFalse: '/system/withdraw/auditFalse' //审核拒绝

    };
  },
  methods: {
    open: function open(id) {
      this.visible = true;
      this.id = id;
      this.refusereason = '';
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.id = '';
      this.refusereason = '';
    },
    handleOk: function handleOk() {
      var that = this;

      if (!this.refusereason || this.refusereason === '') {
        this.$message.warning('请输入拒绝理由！');
      } else {
        that.confirmLoading = true;
        var arr = {
          id: this.id,
          refusereason: this.refusereason
        };
        console.log(arr);
        getAction(this.auditFalse, arr).then(function (res) {
          if (res.success) {
            that.$message.success(res.message);
            that.$emit('ok');
          } else {
            that.$message.warning(res.message);
          }
        }).finally(function () {
          that.confirmLoading = false;
          that.close();
        });
      }
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
