import { getAction } from "@/api/manage";
export default {
  name: "BigTeamAddModal",
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      teamId: null,
      loading: false,
      dataSource: [],
      form: this.$form.createForm(this),
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      // 表头
      columns: [{
        title: '昵称',
        align: "center",
        dataIndex: 'nickName'
      }, {
        title: '手机号',
        align: "center",
        dataIndex: 'phone'
      }, {
        title: 'V云号',
        align: "center",
        dataIndex: 'code'
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: '/system/appuserinfo/findUserListByCode',
        add: '/system/appuserinfo/updateTeamByUserId'
      }
    };
  },
  methods: {
    open: function open(record) {
      this.form.resetFields();
      this.visible = true;
      this.teamId = record.id;
    },
    onSearch: function onSearch(value) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      }

      this.form.validateFields(function (err) {
        if (!err) {
          var params = {
            code: value
          }; //查询条件

          _this.loading = true;
          getAction(_this.url.list, params).then(function (res) {
            if (res.success) {
              _this.dataSource = res.result;
            }

            if (res.code === 510) {
              _this.$message.warning(res.message);
            }

            _this.loading = false;
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
      this.visible = false;
      this.dataSource = [];
    },
    //  添加成员
    lookAdd: function lookAdd(userId) {
      var _this2 = this;

      var param = {
        teamId: this.teamId,
        userId: userId
      };
      var that = this;
      that.confirmLoading = true;
      getAction(this.url.add, param).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);

          _this2.$emit('ok');
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;

        _this2.handleCancel();
      });
    }
  }
};
