import LunboModal from "./modules/LunboModal";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { formatDate } from "@/utils/util";
export default {
  name: "LunboList",
  mixins: [JeecgListMixin],
  components: {
    LunboModal: LunboModal,
    ProductNewBigImg: ProductNewBigImg
  },
  data: function data() {
    return {
      description: '轮播图管理页面',
      queryParam: {
        address: '',
        type: ''
      },
      param: {
        address: '',
        type: ''
      },
      // 表头
      columns: [{
        title: '位置',
        align: "center",
        dataIndex: 'position',
        customRender: function customRender(text) {
          if (text === 1) {
            return '首页';
          } else if (text === 2) {
            return '分类';
          } else if (text === 3) {
            return '直播间';
          }
        }
      }, {
        title: '轮播类型',
        align: "center",
        dataIndex: 'type',
        customRender: function customRender(text) {
          if (text === 1) {
            return '课程/产品';
          } else if (text === 2) {
            return '直播列表';
          } else if (text === 3) {
            return 'url';
          } else if (text === 4) {
            return '排行榜';
          }
        }
      }, {
        title: '地址',
        align: "center",
        dataIndex: 'url'
      }, {
        title: '图片',
        align: "center",
        dataIndex: 'image',
        scopedSlots: {
          customRender: 'image'
        }
      }, {
        title: '时间',
        align: "center",
        dataIndex: 'createTime',
        customRender: function customRender(text) {
          return formatDate(text, 'yyyy-MM-dd hh:mm');
        }
      }, {
        title: '排序',
        align: "center",
        dataIndex: 'sorts'
      }, {
        title: '背景颜色色值',
        align: "center",
        dataIndex: 'bgColor'
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: "/system/lunbo/list",
        delete: "/system/lunbo/delete"
      }
    };
  },
  methods: {}
};
