import { putAction, postAction, httpAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  data: function data() {
    return {
      enable: 0,
      autoSubmit: false,
      visible: false,
      icon: "",
      model: {},
      loading: false,
      title: "新增",
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/que/answer/add",
        edit: "/system/que/answer/update" //修改问题分类

      }
    };
  },
  created: function created() {},
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    add: function add() {
      this.icon = "";
      this.confirmLoading = false;
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      if (record && record.id && record.id !== "" && record.id !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      this.icon = record.icon;
      this.canimg = record.icon;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;

      if (record.parentId === 0) {
        this.model.parentId = "";
      }

      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "question", "answers", "type"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.icon = "";
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var formData = Object.assign(_this2.model, values);

          if (!_this2.model.id) {
            httpurl += _this2.url.add; // let arr = `?name=${formData.name}&icon=${formData.icon}&phone=${formData.phone}`;

            postAction(httpurl, {
              question: formData.question,
              answers: formData.answers,
              type: formData.type
            }).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
                that.confirmLoading = false;
                that.close();
              } else {
                that.$message.warning(res.message);
                that.confirmLoading = false;
              }
            }).finally(function () {});
          } else {
            httpurl += _this2.url.edit; // let arr = `?name=${formData.name}&icon=${formData.icon}&phone=${formData.phone}`;

            putAction(httpurl, {
              question: formData.question,
              answers: formData.answers,
              type: formData.type,
              id: _this2.model.id
            }).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
              } else {
                that.$message.warning(res.message);
              }
            }).finally(function () {
              that.confirmLoading = false;
              that.close();
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
