import ProductCategoryModal from "./modules/ProductCategoryModal";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import ProductNewBigImg from "./modules/ProductNewBigImg";
export default {
  name: "ProductCategoryList",
  mixins: [JeecgListMixin],
  components: {
    ProductCategoryModal: ProductCategoryModal,
    ProductNewBigImg: ProductNewBigImg
  },
  data: function data() {
    return {
      description: "课程/产品分类表管理页面",
      // 表头
      columns: [{
        title: "分类名称",
        align: "center",
        dataIndex: "name"
      }, {
        title: "导航",
        align: "center",
        dataIndex: "icon",
        customRender: function customRender(text) {
          if (text === "y") {
            return "显示";
          } else if (text === "n") {
            return "不显示";
          }
        }
      }, {
        title: "课程图片",
        align: "center",
        dataIndex: "imageUrl",
        scopedSlots: {
          customRender: "avatar"
        }
      }, {
        title: "排序",
        align: "center",
        dataIndex: "sorts"
      }, {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/product/productCategory/rootList",
        delete: "/product/productCategory/delete",
        deleteBatch: "/product/productCategory/deleteBatch",
        exportXlsUrl: "product/productCategory/exportXls",
        importExcelUrl: "product/productCategory/importExcel"
      }
    };
  },
  computed: {
    importExcelUrl: function importExcelUrl() {
      return "".concat(window._CONFIG["domianURL"], "/").concat(this.url.importExcelUrl);
    }
  },
  methods: {
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    }
  }
};
