import Vue from 'vue';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import pick from 'lodash.pick';
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction, httpAction } from "@/api/manage";
import JCodeEditor from "@/components/jeecg/JCodeEditor";
import JEditor from "@/components/jeecg/JEditor";
export default {
  name: "ShareRed",
  mixins: [JeecgListMixin],
  components: {
    JCodeEditor: JCodeEditor,
    JEditor: JEditor
  },
  data: function data() {
    return {
      form: this.$form.createForm(this),
      type: null,
      model: {},
      value: '',
      canimg: '',
      percent: '',
      loading: false,
      headers: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 4
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 19
        }
      },
      columns: [{
        title: '描述',
        align: "center",
        dataIndex: 'descr'
      }],
      baseUrl: '/system/sysparam/',
      url: {
        list: '/system/sysparam/shareProfitList',
        edit: '/system/sysparam/edit'
      }
    };
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  methods: {
    loadData: function loadData(arg) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      } //加载数据 若传入参数1则加载第一页的内容


      if (arg === 1) {
        this.ipagination.current = 1;
      }

      this.loading = true;
      getAction(this.url.list).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result;
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    },
    clickThenCheck: function clickThenCheck(record) {
      var _this2 = this;

      return {
        on: {
          click: function click() {
            _this2.model = Object.assign({}, record);

            _this2.setValuesToForm(record);
          }
        }
      };
    },
    setValuesToForm: function setValuesToForm(record) {
      // this.$nextTick(() => {
      this.form.setFieldsValue(pick(record, 'categoryId', 'chooseFlag', 'code', 'descr', 'id', 'type', 'value')); // });

      this.type = record.type;
      this.value = record.value;
      this.canimg = record.value;
    },
    //重置
    emptyCurrForm: function emptyCurrForm() {
      this.form.resetFields();
    },
    //  提交
    submitCurrForm: function submitCurrForm() {
      var _this3 = this;

      this.form.validateFields(function (err, values) {
        if (!err) {
          if (!_this3.url.edit) {
            return;
          }

          var formData = Object.assign(_this3.model, values);
          formData.value = _this3.value;
          console.log(formData);
          httpAction(_this3.url.edit, formData, 'put').then(function (res) {
            if (res.success) {
              _this3.$message.success(res.message);

              _this3.loadData();
            } else {
              _this3.$message.warning(res.message);
            }
          });
        }
      });
    }
  },
  watch: {}
};
