    // },
    open: function open(record) {
      var _this = this;

      if (record && record.userId && record.userId !== "" && record.userId !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      console.log(record);
      this.teamName = record.teamName;
      this.userId = record.userId;
      this.inviteId = record.inviteId;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "inviteId", "userId", "userName", "teamNum", "nickName", "phone", "code"));
      });
      console.log(this.form);
    },
    close: function close() {
      this.$emit("close");
      this.visible = false;
      this.userId = "";
      this.inviteId = "";
      this.dataSource = [];
      this.searchParam = "";
      this.disableSubmit = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = _this2.url.relation;
          var method = "post"; //   if (!this.model.userId) {
          //     httpurl += this.url.add;
          //     method = "post";
          //   } else {
          //     // httpurl += this.url.edit;
