          //     console.log(111);
          //     alert("用户已经存在！");
          //   }

          var formData = Object.assign(_this2.model, values);
          formData.inviteId = _this2.inviteId;
          console.log("userid" + _this2.userId);
          formData.userId = _this2.userId;
          console.log("inviteId" + formData.inviteId); //时间格式化
          // let arr = `?userId=${this.userId}&inviteId=${this.inviteId}`;
          // postAction(httpurl,{formData}).then(res => {

          httpAction(httpurl + "?userId=" + _this2.userId + "&inviteId=" + _this2.inviteId, null, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit("ok");
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    //  输入选择框
    onSelect: function onSelect(value) {
      var _this3 = this;

      this.dataSource.forEach(function (item) {
        if (item.nickName === value) {
          _this3.inviteId = item.userId;
        }
      });
    },
    // onSelect1(value) {
