import Vue from 'vue';
import { httpAction } from '@/api/manage';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import pick from 'lodash.pick';
import moment from "moment";
import * as qiniu from 'qiniu-js';
export default {
  name: "AppUserInfoModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      loading: false,
      confirmLoading: false,
      disableSubmit: false,
      coverimg: '',
      canimg: '',
      percent: '',
      headers: {},
      form: this.$form.createForm(this),
      validatorRules: {
        userid: {
          rules: [{
            required: true,
            message: '请输入用户ID!'
          }]
        }
      },
      url: {
        add: "/user/appUserInfo/add",
        edit: "/user/appUserInfo/edit",
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken'
      }
    };
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.coverimg = record.header;
      this.canimg = record.header;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'code', 'nickName', 'header', 'credits', 'phone', 'role', 'money', 'balance', 'createTime', 'startTime', 'ambassador', 'endTime'));

        _this.form.setFieldsValue({
          endTime: _this.model.endTime ? moment(_this.model.endTime) : null
        });

        _this.form.setFieldsValue({
          startTime: _this.model.startTime ? moment(_this.model.startTime) : null
        });

        _this.form.setFieldsValue({
          createTime: _this.model.createTime ? moment(_this.model.createTime) : null
        });
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          formData.endtime = formData.endtime ? formData.endtime.format('YYYY-MM-DD') : null;
          formData.sigendtime = formData.sigendtime ? formData.sigendtime.format('YYYY-MM-DD') : null;
          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    customRequest: function customRequest(data) {
      var _this3 = this;

      var suffix = "." + data.file.name.replace(/^.+\./, ''); //请求token

      httpAction(this.url.qiniuToken, {}, 'post').then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this3.url.fileUpload;
          var config = {
            useCdnDomain: true,
            region: null
          };
          var putExtra = {
            fname: '',
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this3.loading = true;
              _this3.percent = res.total.percent.toFixed(2);
              _this3.coverimg = '';
            },
            error: function error() {
              _this3.$message.error('上传失败');

              _this3.loading = false;
              _this3.coverimg = _this3.canimg;
            },
            complete: function complete(response) {
              _this3.coverimg = "".concat(domain, "/").concat(response.key);
              _this3.loading = false;
            }
          });
        }
      });
    }
  }
};
