import flv from 'flv.js';
export default {
  name: 'app',
  data: function data() {
    return {
      player: null,
      playing: false
    };
  },
  created: function created() {
    if (flv.isSupported()) {
      this.player = flv.createPlayer({
        type: 'flv',
        url: 'http://5815.liveplay.myqcloud.com/live/5815_89aad37e06ff11e892905cb9018cf0d4_550.flv'
      });
    }
  },
  mounted: function mounted() {
    var video = document.querySelector('#myvideo');
    this.player.attachMediaElement(video);
    this.player.load();
  },
  methods: {
    click: function click() {
      if (this.playing) {
        this.player.pause();
        this.playing = false;
      } else {
        this.player.play();
        this.playing = true;
      }
    }
  }
};
