import { postAction } from "@/api/manage";
export default {
  name: "AppUserInfoAgentModal",
  data: function data() {
    return {
      title: "设置市代理",
      visible: false,
      loading: false,
      id: '',
      provinceid: null,
      // 省级id
      cityid: null,
      //市级id
      confirmLoading: false,
      disSubmit: false,
      provinceList: [],
      cityList: [],
      url: {
        setAgent: "/system/appuserinfo/setToAgent",
        provinceSelect: '/other/select/provinceSelect',
        // 省级下拉选
        citySelect: '/other/select/citySelect' //市级下拉选

      }
    };
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.id = record.userid;
      this.getPro();
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.id = '';
      this.provinceid = '';
      this.cityid = '';
    },
    handleOk: function handleOk() {
      var that = this;
      that.confirmLoading = true;
      var arr = "?userid=".concat(this.id, "&cityid=").concat(this.cityid);
      var thisurl = this.url.setAgent;
      console.log(thisurl + arr);
      postAction(thisurl + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit('ok');
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
        that.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 获取省级数据
    getPro: function getPro() {
      var that = this;
      postAction(this.url.provinceSelect).then(function (res) {
        if (res.success) {
          that.provinceList = res.result;
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    //  选择的时候获取市级数据
    handelPro: function handelPro(value) {
      var that = this;
      that.cityList = [];
      var arr = "?fatherid=".concat(value);
      postAction(this.url.citySelect + arr).then(function (res) {
        if (res.success) {
          that.cityList = res.result;
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
