import { getAction, deleteAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
import customModals from "./modules/customModals";
export default {
  name: "Customer",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis,
    customModals: customModals
  },
  data: function data() {
    return {
      description: "客服问答页面",
      typeList: [],
      // 表头
      columns: [{
        title: "问题",
        align: "center",
        dataIndex: "question",
        width: 450,
        scopedSlots: {
          customRender: "templateTitle"
        }
      }, {
        title: "回答",
        align: "center",
        dataIndex: "answers",
        width: 450,
        scopedSlots: {
          customRender: "templateTitle"
        }
      }, {
        title: "创建时间",
        align: "center",
        dataIndex: "createTime"
      }, {
        title: "问题类型",
        align: "center",
        dataIndex: "type",
        customRender: function customRender(text) {
          if (text == 1) {
            return "充值";
          } else if (text == 2) {
            return "课程";
          } else if (text == 3) {
            return "群聊";
          } else if (text == 4) {
            return "提现";
          }
        }
      }, {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/system/que/answer/list",
        delete: "/system/que/answer/delete"
      }
    };
  },
  created: function created() {},
  methods: {
    handleDelete: function handleDelete(record) {
      var id = record.id;

      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }

      var that = this;
      deleteAction(that.url.delete, {
        id: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
