import moment from "moment";
import { postAction, getAction } from "@/api/manage";
import { filterObj, formatDate } from "@/utils/util";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import ChartCard from "@/components/ChartCard";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "RewardAnchor",
  mixins: [JeecgListMixin],
  components: {
    ProductNewBigImg: ProductNewBigImg,
    JEllipsis: JEllipsis,
    ChartCard: ChartCard
  },
  data: function data() {
    return {
      description: "用户打赏主播礼物列表",
      dataSource: [],
      giftOrderCount: 0,
      giftOrderVCoinMoney: 0,
      queryParam: {
        createTimeRange: [],
        logType: "0",
        beginTime: "",
        endTime: "",
        operType: ""
      },
      param: {
        createTimeRange: [],
        logType: "0",
        beginTime: "",
        endTime: "",
        operType: ""
      },
      columns: [{
        title: "订单编号",
        align: "center",
        dataIndex: "orderNo"
      }, {
        title: "用户名称",
        align: "center",
        dataIndex: "nickName"
      }, {
        title: "房间号",
        align: "center",
        dataIndex: "code"
      }, {
        title: "主播获得V钻",
        align: "center",
        dataIndex: "balance"
      }, {
        title: "礼物图标",
        align: "center",
        dataIndex: "icon",
        scopedSlots: {
          customRender: "header"
        }
      }, {
        title: "打赏礼物单价V币",
        align: "center",
        dataIndex: "money"
      }, {
        title: "礼物名称",
        align: "center",
        dataIndex: "name"
      }, {
        title: "打赏礼物数量",
        align: "center",
        dataIndex: "number"
      }, {
        title: "直播用户昵称",
        align: "center",
        dataIndex: "liveNickName"
      }, {
        title: "用户标识",
        align: "center",
        dataIndex: "marker",
        customRender: function customRender(text) {
          if (text == 0) {
            return "测试";
          } else if (text == 1) {
            return "正式";
          } else if (text == 2) {
            return "官方";
          }
        }
      }, {
        title: "创建时间",
        align: "center",
        dataIndex: "createTime",
        scopedSlots: {
          customRender: "createTime"
        }
      }],
      url: {
        list: "/system/live/gitOrder/list"
      }
    };
  },
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    loadData: function loadData(arg) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      } //加载数据 若传入参数1则加载第一页的内容


      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //查询条件

      this.loading = true;
      getAction(this.url.list, params).then(function (res) {
        if (res.success) {
          console.log(res);
          _this.dataSource = res.result.listVoIPage.records;
          _this.ipagination.total = res.result.listVoIPage.total;
          _this.total = res.result.listVoIPage.total;
          _this.giftOrderCount = res.result.giftOrderCount;
          _this.giftOrderVCoinMoney = res.result.giftOrderVCoinMoney;
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    },
    handleMembersList: function handleMembersList(record) {
      this.$refs.modalForm.open(record);
    },
    getQueryParams: function getQueryParams() {
      console.log(this.queryParam.createTimeRange);
      var param = Object.assign({}, this.queryParam, this.isorter);
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.createTimeRange = this.createTimeRange;
      param.beginTime = this.queryParam.beginTime;
      param.endTime = this.queryParam.endTime;
      return filterObj(param);
    },
    onDateChange: function onDateChange(value, dateString) {
      console.log(dateString[0], dateString[1]);
      this.queryParam.beginTime = dateString[0] + " 00:00:00";
      this.queryParam.endTime = dateString[1] + " 00:00:00";
    },
    onDateOk: function onDateOk(value) {
      console.log(value);
    }
  }
};
