import ProductNewAddModal from "./modules/ProductNewAddModal"; // import ProductNewModal from './modules/ProductNewModal'

import ProductNewBigImg from "./modules/ProductNewBigImg";
import ProductNewAuditModel from "./modules/ProductNewAuditModel";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { getAction } from "@/api/manage";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "ProductNewList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis,
    ProductNewAddModal: ProductNewAddModal,
    // ProductNewModal,
    ProductNewBigImg: ProductNewBigImg,
    ProductNewAuditModel: ProductNewAuditModel
  },
  data: function data() {
    return {
      description: "课程管理页面",
      selectrue: false,
      // 表头
      columns: [{
        title: "课程标题",
        align: "center",
        dataIndex: "title",
        scopedSlots: {
          customRender: "templateTitle"
        }
      }, {
        title: "课程简介",
        align: "center",
        dataIndex: "introduction",
        scopedSlots: {
          customRender: "templateContent"
        }
      }, {
        title: "课程作者",
        align: "center",
        dataIndex: "author"
      }, {
        title: "课程分类",
        align: "center",
        dataIndex: "category"
      }, {
        title: "课程封面",
        align: "center",
        dataIndex: "coverimg",
        scopedSlots: {
          customRender: "avatar"
        }
      }, {
        title: "浏览次数",
        align: "center",
        dataIndex: "clickcount"
      }, {
        title: "发布日期",
        align: "center",
        dataIndex: "createTime"
      }, {
        title: "操作",
        dataIndex: "action",
        align: "center",
        scopedSlots: {
          customRender: "action"
        }
      }],
      selectList: [],
      selecBox: [],
      url: {
        list: "/product/productNew/list",
        delete: "/product/productNew/delete",
        //查询分类接口
        selectNewList: "/product/productCategory/queryFirstSelect",
        auditTrue: "/product/productNew/auditTrue",
        //审核通过
        commen: "/product/productNew/recommended",
        // 推荐设置
        querySecondSelect: "/product/productCategory/querySecondSelect"
      }
    };
  },
  created: function created() {
    var _this = this;

    //获取分类查询数据
    getAction(this.url.selectNewList).then(function (res) {
      if (res.success) {
        console.log("res");
        console.log(res);
        _this.selectList = res.result;
      } else {
        _this.$message.warning(res.message);
      }
    });
  },
  methods: {
    typeSelectHandle: function typeSelectHandle(value) {
      var _this2 = this;

      this.selectList.forEach(function (item) {
        if (item.id === value) {
          _this2.cateName = item.name;
          console.log(item.id);
        }
      });
      this.fatherId = value;
      getAction(this.url.querySecondSelect, {
        fatherId: this.fatherId
      }).then(function (res) {
        if (res.success) {
          console.log(res.result);

          if (res.result != "") {
            _this2.selecBox = res.result;
            _this2.selectrue = true;
            console.log(_this2.selecBox);
          } else {
            _this2.selectrue = false;
          }
        } else {
          _this2.$message.warning(res.message);
        }
      });
    },
    cateoryHandle2: function cateoryHandle2(value) {
      var _this3 = this;

      console.log(value);
      this.selectList.forEach(function (item) {
        if (item.id === value) {
          _this3.cateName = item.name;
          console.log(item.id);
        }
      });
    },
    clickThenCheck: function clickThenCheck(record) {
      var _this4 = this;

      return {
        on: {
          click: function click() {
            _this4.$refs.modalForm.edit(record);

            _this4.$refs.modalForm.title = "详情";
            _this4.$refs.modalForm.disableSubmit = true;
          }
        }
      };
    },
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    //  审核通过，请求接口
    handleAuditTrue: function handleAuditTrue(id) {
      if (!this.url.auditTrue) {
        this.$message.error("请设置审核属性!");
        return;
      }

      var that = this;
      getAction(this.url.auditTrue, {
        id: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    //  审核拒绝，弹出框=>拒绝理由
    handleShenJu: function handleShenJu(id) {
      this.$refs.auditModel.open(id);
    },
    //  推荐设置
    handleCommen: function handleCommen(id, level, set) {
      var arr = {
        id: id,
        level: level,
        set: set
      };
      var that = this;
      getAction(this.url.commen, arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }

        that.loadData();
      });
    }
  }
};
