import { postAction } from "@/api/manage";
export default {
  name: "VyunBransh",
  data: function data() {
    return {
      visible: false,
      userid: null,
      confirmLoading: false,
      type: "",
      url: {
        setAmbassador: "/system/appuserinfo/setAmbassador" // 设置V云大使

      }
    };
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.userid = record.userid;
      this.type = 2;
    },
    close: function close() {
      this.$emit("close");
      this.visible = false;
      this.userid = null;
      this.type = null;
    },
    handleOk: function handleOk() {
      var arr = "?userid=".concat(this.userid, "&type=").concat(this.type);
      var that = this;
      postAction(this.url.setAmbassador + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
        that.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
