import { httpAction, getAction } from '@/api/manage';
import Vue from 'vue';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import pick from 'lodash.pick';
import * as qiniu from 'qiniu-js';
export default {
  name: "BigTeamModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      headers: {},
      loading: false,
      coverimg: '',
      canimg: '',
      percent: '',
      disableSubmit: false,
      autoSubmit: false,
      teamName: '',
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      searchParam: '',
      // 搜索字段
      dataSource: [],
      // 选择数据源
      userId: null,
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/bigteam/add",
        edit: "/system/bigteam/edit",
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken',
        searchUrl: '/system/appuserinfo/getUserBYPhoneOrCodeOrNickName'
      }
    };
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  methods: {
    add: function add() {
      this.coverimg = '';
      this.teamName = '';
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      console.log(record.name);

      if (record && record.name && record.name !== '' && record.name !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      this.teamName = record.userName;
      this.coverimg = record.image;
      this.canimg = record.image;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'name', 'image', 'userId', 'userName', 'teamNum', 'descr'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.coverimg = '';
      this.canimg = '';
      this.percent = '';
      this.dataSource = [];
      this.searchParam = '';
      this.disableSubmit = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log('formData');
          console.log(formData);
          formData.image = _this2.coverimg;
          formData.userId = _this2.userId; //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    customRequest: function customRequest(data) {
      var _this3 = this;

      var suffix = "." + data.file.name.replace(/^.+\./, ''); //请求token

      httpAction(this.url.qiniuToken, {}, 'post').then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this3.url.fileUpload;
          var config = {
            useCdnDomain: true,
            region: null
          };
          var putExtra = {
            fname: '',
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this3.loading = true;
              _this3.percent = res.total.percent.toFixed(2);
              _this3.coverimg = '';
            },
            error: function error() {
              _this3.$message.error('上传失败');

              _this3.loading = false;
              _this3.coverimg = _this3.canimg;
            },
            complete: function complete(response) {
              _this3.coverimg = "".concat(domain, "/").concat(response.key);
              _this3.loading = false;
            }
          });
        }
      });
    },
    //  输入选择框
    onSelect: function onSelect(value) {
      var _this4 = this;

      this.dataSource.forEach(function (item) {
        if (item.nickName === value) {
          _this4.userId = item.userId;
          console.log('this.userId');
          console.log(_this4.userId);
        }
      });
    },
    handleSearch: function handleSearch(value) {
      console.log('search', value);
      this.searchParam = value;
    },
    //  开始搜索
    beginSearch: function beginSearch() {
      var _this5 = this;

      getAction(this.url.searchUrl, {
        searchParam: this.searchParam
      }).then(function (res) {
        console.log(res);

        if (res.success) {
          if (res.result.length === 0) {
            _this5.$message.warning('暂无数据');
          } else {
            _this5.dataSource = res.result;
          }
        } else {
          _this5.$message.warning(res.message);
        }
      }).catch(function (err) {
        _this5.$message.warning(err.message);
      });
    }
  }
};
