import { putAction, postAction, httpAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "detailModals",
  data: function data() {
    return {
      enable: 0,
      autoSubmit: false,
      visible: false,
      icon: "",
      model: {},
      loading: false,
      title: "新增",
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/que/answer/add",
        edit: "/system/que/answer/update" //修改问题分类

      }
    };
  },
  methods: {
    add: function add() {
      this.icon = "";
      this.confirmLoading = false;
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      if (record && record.id && record.id !== "" && record.id !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      this.icon = record.icon;
      this.canimg = record.icon;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;

      if (record.parentId === 0) {
        this.model.parentId = "";
      }

      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "question", "answers", "type"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.icon = "";
      this.visible = false;
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
