import Vue from 'vue';
import router from "./router";
import store from "./store";
import NProgress from 'nprogress'; // progress bar

import 'nprogress/nprogress.css'; // progress bar style

import notification from 'ant-design-vue/es/notification';
import { ACCESS_TOKEN } from '@/store/mutation-types';
import { generateIndexRouter } from "@/utils/util";
NProgress.configure({
  showSpinner: false
}); // NProgress Configuration

var whiteList = ['/user/login', '/user/register', '/user/register-result', '/user/alteration']; // no redirect whitelist

router.beforeEach(function (to, from, next) {
  NProgress.start(); // start progress bar

  if (Vue.ls.get(ACCESS_TOKEN)) {
    /* has token */
    if (to.path === '/user/login' || to.path === '/login') {
      next({
        path: '/dashboard/analysis'
      });
      NProgress.done();
    } else {
      if (store.getters.permissionList.length === 0) {
        store.dispatch('GetPermissionList').then(function (res) {
          var menuData = res.result.menu;
          console.log(res.message);

          if (menuData === null || menuData === "" || menuData === undefined) {
            return;
          }

          var constRoutes = [];
          constRoutes = generateIndexRouter(menuData); // 添加主界面路由

          store.dispatch('UpdateAppRouter', {
            constRoutes: constRoutes
          }).then(function () {
            // 根据roles权限生成可访问的路由表
            // 动态添加可访问路由表
            router.addRoutes(store.getters.addRouters);
            var redirect = decodeURIComponent(from.query.redirect || to.path);

            if (to.path === redirect) {
              // hack方法 确保addRoutes已完成 ,set the replace: true so the navigation will not leave a history record
              next(_objectSpread(_objectSpread({}, to), {}, {
                replace: true
              }));
            } else {
              // 跳转到目的路由
              next({
                path: redirect
              });
            }
          });
        }).catch(function () {
          /* notification.error({
             message: '系统提示',
             description: '请求用户信息失败，请重试！'
           })*/
          store.dispatch('Logout').then(function () {
            next({
              path: '/user/login',
              query: {
                redirect: to.fullPath
              }
            });
          });
        });
      } else {
        next();
      }
    }
  } else {
    if (whiteList.indexOf(to.path) !== -1) {
      // 在免登录白名单，直接进入
      next();
    } else {
      next({
        path: '/user/login',
        query: {
          redirect: to.fullPath
        }
      });
      NProgress.done(); // if current page is login will not trigger afterEach hook, so manually handle it
    }
  }
});
router.afterEach(function () {
  NProgress.done(); // finish progress bar
});
