import { filterObj } from "@/utils/util";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
export default {
  name: "ChatGroupMemberModal",
  mixins: [JeecgListMixin],
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      teamId: null,
      loading: false,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 7
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      // 表头
      columns: [{
        title: '用户V云id',
        align: "center",
        dataIndex: 'code'
      }, {
        title: '用户环信id',
        align: "center",
        dataIndex: 'easemobId'
      }, {
        title: '角色',
        align: "center",
        dataIndex: 'role',
        customRender: function customRender(text) {
          if (text === 1) {
            return '群主';
          } else if (text === 2) {
            return '管理员';
          } else if (text === 3) {
            return '普通成员';
          }
        }
      }, {
        title: '进入时间',
        align: "center",
        dataIndex: 'createTime'
      }, {
        title: '是否禁言',
        align: "center",
        dataIndex: 'bannedPost',
        customRender: function customRender(text) {
          if (text === 0) {
            return '否';
          } else if (text === 1) {
            return '是';
          }
        }
      }],
      url: {
        list: '/system/chatgroupMember/memberList'
      }
    };
  },
  methods: {
    getQueryParams: function getQueryParams() {
      //获取查询条件
      var sqp = {};

      if (this.superQueryParams) {
        sqp['superQueryParams'] = encodeURI(this.superQueryParams);
      }

      var param = Object.assign(sqp, this.queryParam, this.isorter, this.filters);
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.groupId = this.teamId;
      param.bp = param.bp === 0 || param.bp === 1 ? param.bp : '';
      param.role = param.role ? param.role : '';
      param.code = param.code ? param.code : '';
      param.huanxinId = param.huanxinId ? param.huanxinId : '';
      return filterObj(param);
    },
    open: function open(record) {
      this.visible = true;
      this.teamId = record.id;
      this.queryParam = {};
      this.ipagination.current = 1;
      this.loadData();
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
      this.visible = false;
      this.dataSource = [];
    }
  }
};
