import { putAction } from "@/api/manage";
export default {
  name: "aduitModal",
  data: function data() {
    return {
      title: "审核状态",
      visible: false,
      reason: "",
      typevulue: 18,
      state: 1,
      confirmLoading: false,
      url: {
        audit: "/system/mall/goods/audit"
      }
    };
  },
  watch: {
    reason: function reason() {
      if (this.reason.length > 20) {
        alert("审核原因最多不能超过20个字符");
        this.reason = String(this.reason).slice(0, 20);
        return;
      } else if (this.reason.length == 0) {
        this.reason == "";
      }
    }
  },
  methods: {
    open: function open(record) {
      this.visible = true;
      this.goodId = record.id;
      this.state = 1;
      this.reason == "";
    },
    close: function close() {
      this.$emit("close");
      this.visible = false;
      this.state = null;
      this.reason = '';
    },
    // 点击确定后的回调函数
    handleOk: function handleOk() {
      var that = this;
      var arr = "";
      that.confirmLoading = true;
      arr = "?goodId=".concat(this.goodId, "&state=").concat(this.state, "&reason=").concat(this.reason);
      console.log(arr);
      putAction(this.url.audit + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit("ok");
        } else {
          that.$message.warning(res.message);
        }
      }).finally(function () {
        that.confirmLoading = false;
        that.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
