    makeEdit: function makeEdit() {
      var _this8 = this;

      // 判断详情类型
      //类型1纯文字、2纯图片、3纯视频、4纯音频、5图文、6视频与图文、7音频与图文
      var record = this.record;
      var contentType = record.contentType;
      var thisHtml = "";
      var typeImgs = [];

      switch (contentType) {
        case 1:
          this.content = record.content;
          break;

        case 2:
          typeImgs = record.images.split(";");

          for (var i = 0; i < typeImgs.length; i++) {
            thisHtml = "<p><img src=\"".concat(typeImgs[i], "\" /></p>");
          }

          this.content = thisHtml;
          break;

        case 3:
          if (this.title === "详情" && record.videoImage && record.videoImage !== "") {
