      this.$nextTick(function () {
        _this8.form.setFieldsValue(pick(_this8.model, "title", "introduction", "price", "type", "fistcategory", "secondcategory", "content", "coverimg", "album", "createTime", "clickcount")); //  this.form.setFieldsValue({createTime:this.model.createTime?moment(this.model.createTime):null})

