              var coverimg = allUrl;

              if (fileType.indexOf("image") < 0) {
                //  视频资源时需要请求接口获取时长
                getAction(allUrl + "?avinfo").then(function (res) {
                  // 时长
                  var makeDuration = res.format.duration;

                  var thisDuration = _this12.setDuration(makeDuration);

                  var dataCon = {
                    type: _this12.thisType,
                    timeurl: thisDuration,
                    title: "",
                    url: coverimg
                  };

                  _this12.contentList.splice(_this12.thisIndex, 0, dataCon);

                  _this12.$forceUpdate();
                });
              } else {
                var dataCon = {
                  type: _this12.thisType,
                  timeurl: "",
                  title: "",
                  url: coverimg
                };

                _this12.contentList.splice(_this12.thisIndex, 0, dataCon);

                console.log("this.contentList");
                console.log(_this12.contentList);

                _this12.$forceUpdate();
              }

              _this12.confirmLoading = false;
              _this12.filePercent = "";
            }
          });
