    setDuration: function setDuration(dur) {
      // 秒数取整
      var intTime = parseInt(dur);
      var fen = "0";
      var miao = "0"; // 小于10秒， 格式  00:0_

      if (intTime < 10) {
        return "00:0".concat(intTime);
      } // 大于10秒小于60秒， 格式   00:_


      if (intTime < 60) {
        return "00:".concat(intTime);
      } // 大于1分钟小于60分钟  格式  _:_


      if (intTime < 3600) {
        var mins = parseInt(intTime / 60);
        var sec = intTime % 60;

        if (mins < 10) {
          fen = "0".concat(mins);
        } else {
          fen = "".concat(mins);
        }

        if (sec < 10) {
          miao = "0".concat(sec);
        } else {
          miao = "".concat(sec);
        }

        return "".concat(fen, ":").concat(miao);
      } // 大于60分钟   格式  _:_:_


      if (intTime >= 3600) {
        var hour = parseInt(intTime / 3600);

        var _mins = parseInt(intTime % 3600 / 60);

        var _sec = intTime % 3600 % 60 % 60;

        if (_mins < 10) {
          fen = "0".concat(_mins);
        } else {
          fen = "".concat(_mins);
        }

        if (_sec < 10) {
          miao = "0".concat(_sec);
        } else {
          miao = "".concat(_sec);
        }

        return "".concat(hour, ":").concat(fen, ":").concat(miao);
      }
    }
  }
