import { putAction, postAction, httpAction, getAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "OrderDetail",
  data: function data() {
    return {
      enable: 0,
      autoSubmit: false,
      visible: false,
      icon: "",
      orderId: '',
      model: {},
      percent: "",
      // 图片上传进度
      loading: false,
      title: "新增",
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        edit: "/system/mall/order/detail" //修改问题分类

      }
    };
  },
  created: function created() {},
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    makeLength: function makeLength(rule, value, callback) {
      if (value.length > 10) {
        callback("输入字符限制，请重新输入");
      } else {
        callback();
      }
    },
    makephone: function makephone(rule, value, callback) {
      if (value.length > 11) {
        callback("请输入正确得手机号");
      } else {
        callback();
      }
    },
    edit: function edit(record) {
      var _this = this;

      this.orderId = record.id;

      if (this.orderId) {
        this.autoSubmit = true;
      } else {
        this.autoSubmit = false;
      }

      this.icon = record.icon;
      this.canimg = record.icon;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "consigneeName", "consigneePhone", "consigneeAddress", "orderNo", "courierCompany", "courierNo", "goodsName", "goodsNum", "totalMoney", "payMoney", "source", "state", "tradeNo", "payMode", "remark"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.percent = "";
      this.icon = "";
      this.visible = false;
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
