import { postAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
import UserUnderList from "./modules/UserUnderList";
export default {
  name: "AppUserUnder",
  mixins: [JeecgListMixin],
  components: {
    UserUnderList: UserUnderList,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      inviteId: "",
      description: "伞下用户",
      type: "ambassadorList1",
      // queryParam: {
      //   rate: this.total
      // },
      columns: [{
        title: "V云号",
        align: "center",
        dataIndex: "code"
      }, {
        title: "用户昵称",
        align: "center",
        dataIndex: "nickName"
      }, {
        title: "手机号",
        align: "center",
        dataIndex: "phone"
      }, {
        title: "伞下用户充值总费用",
        align: "center",
        dataIndex: "totalMoney"
      }, {
        title: "分润比例",
        align: "center",
        dataIndex: "rate",
        scopedSlots: {
          customRender: "fenRate"
        }
      }, {
        title: "分润所得",
        align: "center",
        dataIndex: "shareProfitMoney",
        scopedSlots: {
          customRender: "share"
        }
      }, {
        title: "伞下人数",
        align: "center",
        dataIndex: "userCount"
      }, {
        title: "操作",
        align: "center",
        dataIndex: "inviteId",
        width: 180,
        scopedSlots: {
          customRender: "action"
        }
      }],
      url: {
        list: "/system/appuserinfo/user-exists-umbrella-list",
        share: "/system/appuserinfo/umbrella-user-share-profit"
      }
    };
  },
  methods: {
    allFr: function allFr() {
      if (this.selectedRowKeys.length <= 0) {
        this.$message.warning("请选择一条记录！");
        return;
      } else {
        var that = this;
        var rids = "";

        for (var a = 0; a < this.selectedRowKeys.length; a++) {
          rids += this.selectedRowKeys[a] + ",";
        }

        var rate = that.$refs.name.value;
        var arr = "?rids=".concat(rids, "&rate=").concat(rate);
        postAction(that.url.share + arr).then(function (res) {
          if (res.success) {
            that.$message.success(res.message);
            that.loadData();
            that.onClearSelected();
          } else {
            that.$message.warning(res.message);
          }
        });
      }
    },
    // 单个分润
    shareRate: function shareRate(record) {
      var that = this;
      record.rate = that.$refs.name.value;
      var arr = "?rids=".concat(record.rids, "&rate=").concat(record.rate);
      postAction(that.url.share + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          console.log(res);
          that.loadData();
          that.onClearSelected();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    handleMembersList: function handleMembersList(record) {
      console.log(record);
      this.$refs.modalForm.open(record);
    }
  }
};
