import { postAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "AppUserInfoRoleModel",
  data: function data() {
    return {
      title: "修改角色",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      loading: false,
      confirmLoading: false,
      disSubmit: false,
      form: this.$form.createForm(this),
      url: {
        setRole: "/system/appuserinfo/setRole"
      }
    };
  },
  methods: {
    open: function open(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'role', 'startTime', 'endTime'));

        _this.form.setFieldsValue({
          endTime: _this.model.endTime ? moment(_this.model.endTime) : null
        });

        _this.form.setFieldsValue({
          startTime: _this.model.startTime ? moment(_this.model.startTime) : null
        });
      });

      if (record.role == '0') {
        this.disSubmit = true;
      } else {
        this.disSubmit = false;
      }
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = _this2.url.setRole;
          var arr = '';
          var formData = Object.assign(_this2.model, values); //时间格式化

          formData.endTime = formData.endTime ? formData.endTime.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.startTime = formData.startTime ? formData.startTime.format('YYYY-MM-DD HH:mm:ss') : null;

          if (that.disSubmit) {
            arr = "?userid=".concat(formData.userid, "&role=").concat(formData.role);
          } else {
            arr = "?userid=".concat(formData.userid, "&role=").concat(formData.role, "&startTime=").concat(formData.startTime, "&endTime=").concat(formData.endTime);
          }

          console.log(formData);
          console.log(arr);
          postAction(httpurl + arr).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    handelSelect: function handelSelect(value) {
      if (value == '0') {
        this.disSubmit = true;
      } else {
        this.disSubmit = false;
      }
    }
  }
};
