import { httpAction, getAction } from "@/api/manage";
import pick from "lodash.pick";
export default {
  name: "OfficialListModal1",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      headers: {},
      loading: false,
      disableSubmit: false,
      autoSubmit: false,
      //   teamName: "",
      userId: "",
      invitedUserId: "",
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      searchParam: "",
      // 搜索字段
      dataSource: [],
      // 选择数据源
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/appuserinfo/add-vip-giving",
        edit: "/system/bigteam/edit",
        // qiniuToken: "/qiniuUpToken",
        searchUrl: "/system/appuserinfo/getUserBYPhoneOrCodeOrNickName"
      }
    };
  },
  methods: {
    add: function add() {
      //   this.teamName = "";
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      if (record && record.userId && record.userId !== "" && record.userId !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      console.log(record);
      this.teamName = record.teamName;
      this.userId = record.userId;
      this.invitedUserId = record.invitedUserId;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "invitedUserId", "userId", "userName", "teamNum"));
      });
      console.log(this.form);
    },
    close: function close() {
      this.$emit("close");
      this.visible = false;
      this.userId = "";
      this.invitedUserId = "";
      this.dataSource = [];
      this.searchParam = "";
      this.disableSubmit = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var method = "";

          if (!_this2.model.userId) {
            httpurl += _this2.url.add;
            method = "post";
          } else {
            // httpurl += this.url.edit;
            // method = "put";
            console.log(111);
            alert("用户已经存在！");
          }

          var formData = Object.assign(_this2.model, values);
          formData.invitedUserId = _this2.invitedUserId;
          formData.userId = _this2.userId;
          console.log("invitedUserId" + formData.invitedUserId);
          console.log("userid" + formData.userId); //时间格式化
          // let arr = `?userId=${this.userId}&invitedUserId=${this.invitedUserId}`;
          // postAction(httpurl,{formData}).then(res => {

          httpAction(httpurl + "?userId=" + _this2.userId + "&invitedUserId=" + _this2.invitedUserId, null, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit("ok");
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    //  输入选择框
    onSelect: function onSelect(value) {
      var _this3 = this;

      this.dataSource.forEach(function (item) {
        if (item.nickName === value) {
          _this3.userId = item.userId;
        }
      });
    },
    onSelect1: function onSelect1(value) {
      var _this4 = this;

      this.dataSource.forEach(function (item) {
        if (item.nickName === value) {
          _this4.invitedUserId = item.userId;
        }
      });
    },
    handleSearch: function handleSearch(value) {
      //   console.log("search", value);
      this.searchParam = value;
    },
    //  开始搜索
    beginSearch: function beginSearch() {
      var _this5 = this;

      getAction(this.url.searchUrl, {
        searchParam: this.searchParam
      }).then(function (res) {
        console.log(res);

        if (res.success) {
          if (res.result.length === 0) {
            _this5.$message.warning("暂无数据");
          } else {
            _this5.dataSource = res.result;
          }
        } else {
          _this5.$message.warning(res.message);
        }

        console.log(11111 + res);
      }).catch(function (err) {
        _this5.$message.warning(err.message);
      });
    }
  }
};
