import AppUserInfoModal from "./modules/AppUserInfoModal";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import AppUserInfoChangeModel from "./modules/AppUserInfoChangeModel";
import AppSetVip from "./modules/AppSetVip";
import AppSetCreator from "./modules/AppSetCreator";
import AppSetAmbassador from "./modules/AppSetAmbassador";
import AppUserInfoRoleModel from "./modules/AppUserInfoRoleModel";
import AppUserInfoAgentModal from "./modules/AppUserInfoAgentModal";
import exportExcelModel from "./modules/exportExcelModel";
import ShareId from "./modules/ShareId";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { getAction, postAction } from "@/api/manage";
export default {
  name: "AppUserInfoList",
  mixins: [JeecgListMixin],
  components: {
    AppUserInfoModal: AppUserInfoModal,
    ProductNewBigImg: ProductNewBigImg,
    AppUserInfoChangeModel: AppUserInfoChangeModel,
    AppSetVip: AppSetVip,
    AppSetCreator: AppSetCreator,
    AppSetAmbassador: AppSetAmbassador,
    AppUserInfoRoleModel: AppUserInfoRoleModel,
    AppUserInfoAgentModal: AppUserInfoAgentModal,
    exportExcelModel: exportExcelModel,
    ShareId: ShareId
  },
  data: function data() {
    return {
      description: "用户详情管理页面",
      queryParam: {
        isShareProfit: 0,
        code: null,
        name: null // role: null,
        // ambassador: null,
        // marker: null

      },
      param: {
        isShareProfit: 0,
        code: null,
        name: null // role: null,
        // ambassador: null,
        // marker: null

      },
      // 表头
      columns: [{
        title: "手机号",
        align: "center",
        dataIndex: "phone"
      }, {
        title: "V云号",
        align: "center",
        dataIndex: "code"
      }, {
        title: "昵称",
        align: "center",
        dataIndex: "nickName"
      }, {
        title: "头像",
        align: "center",
        dataIndex: "header",
        scopedSlots: {
          customRender: "header"
        }
      }, {
        title: "角色",
        align: "center",
        dataIndex: "role",
        customRender: function customRender(text) {
          if (text == "0") {
            return "普通会员";
          } else if (text == "1") {
            return "VIP会员";
          } else if (text == "2") {
            return "大V会员";
          }
        }
      }, {
        title: "创客达人",
        align: "center",
        dataIndex: "isCreator",
        customRender: function customRender(text) {
          if (text == 1) {
            return "是";
          } else {
            return "否";
          }
        }
      }, {
        title: "V云大使",
        align: "center",
        dataIndex: "ambassador",
        customRender: function customRender(text) {
          if (text == 1) {
            return "是";
          } else {
            return "否";
          }
        }
      }, {
        title: "大使星级",
        align: "center",
        dataIndex: "ambassadorLevel"
      }, {
        title: "V币",
        align: "center",
        dataIndex: "money"
      }, {
        title: "V钻",
        align: "center",
        dataIndex: "balance"
      }, {
        title: "提现冻结金额",
        align: "center",
        dataIndex: "frozenBalance"
      }, {
        title: "订单冻结金额",
        align: "center",
        dataIndex: "draw"
      }, {
        title: "大V值",
        align: "center",
        dataIndex: "credits"
      }, {
        title: "用户标识",
        align: "center",
        dataIndex: "marker",
        customRender: function customRender(text) {
          if (text == 0) {
            return "测试";
          } else if (text == 1) {
            return "正式";
          } else if (text == 2) {
            return "官方";
          }
        }
      }, {
        title: "创建时间",
        align: "center",
        dataIndex: "createTime"
      }, {
        title: "VIP开始时间",
        align: "center",
        dataIndex: "startTime"
      }, {
        title: "VIP结束时间",
        align: "center",
        dataIndex: "endTime"
      }, {
        title: "操作",
        dataIndex: "action1",
        align: "center",
        scopedSlots: {
          customRender: "action1"
        }
      }],
      url: {
        list: "/system/appuserinfo/list",
        delete: "/user/appUserInfo/delete",
        deleteBatch: "/user/appUserInfo/deleteBatch",
        exportXlsUrl: "user/appUserInfo/exportXls",
        importExcelUrl: "user/appUserInfo/importExcel",
        setCreator: "/system/appuserinfo/setCreator",
        commoned: "/system/appuserinfo/recommended",
        setAmbassador: "/system/appuserinfo/setAmbassador",
        cancelCreator: "/system/appuserinfo/cancel-creator",
        //取消创客达人
        cancelAambassador: "/system/appuserinfo/cancel-ambassador",
        //取消v云大使
        exportExcel: "/user/excel/exportExcel"
      }
    };
  },
  created: function created() {
    this.loadData();
  },
  computed: {
    importExcelUrl: function importExcelUrl() {
      return "".concat(window._CONFIG["domianURL"], "/").concat(this.url.importExcelUrl);
    }
  },
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    //  修改角色
    handelRole: function handelRole(record) {
      this.$refs.roleModel.open(record);
    },
    //  设置V云大使
    handleAmbassador: function handleAmbassador(userid) {
      var arr = "?userid=".concat(userid);
      var that = this;
      postAction(this.url.setAmbassador + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    },
    //  修改
    handleAppChange: function handleAppChange(record, type) {
      this.$refs.changeModel.open(record);
      this.$refs.changeModel.type = type; // 修改V币

      if (type === "setMoney") {
        this.$refs.changeModel.title = "V币";
        this.$refs.changeModel.typevulue = record.money;
        this.$refs.changeModel.defaultIsVip = false;
      } else if (type === "setBalance") {
        // 修改V钻
        this.$refs.changeModel.title = "V钻";
        this.$refs.changeModel.typevulue = record.balance;
        this.$refs.changeModel.defaultIsVip = false;
      } else if (type === "setCredits") {
        // 修改大V值
        this.$refs.changeModel.title = "大V值";
        this.$refs.changeModel.typevulue = record.credits;
        this.$refs.changeModel.defaultIsVip = false;
      } else if (type === "setMarker") {
        // 修改大V值
        this.$refs.changeModel.title = "标识";
        this.$refs.changeModel.typevulue = record.marker;
        this.$refs.changeModel.defaultIsVip = true;
      } // else if (type === "setVip") {
      //   //  设置会员
      //   this.$refs.changeModel1.title = "会员";
      //   this.$refs.changeModel1.typevulue = null;
      //   this.$refs.changeModel1.defaultIsVip = true;
      // }

    },
    // 设置会员
    handleAppChange1: function handleAppChange1(record) {
      this.$refs.changeModel1.title = "会员";
      this.$refs.changeModel1.open(record);
      this.$refs.changeModel1.defaultIsVip = true;
    },
    // 设置创客达人
    handleAppChange2: function handleAppChange2(record) {
      this.$refs.changeModel2.title = "创客达人";
      this.$refs.changeModel2.open(record);
      this.$refs.changeModel2.defaultIsVip = true;
    },
    // 设置v云大使
    handleAppChange3: function handleAppChange3(record) {
      this.$refs.changeModel3.title = "v云大使";
      this.$refs.changeModel3.open(record);
      this.$refs.changeModel3.defaultIsVip = true;
    },
    // 设置靓号
    handleShareId: function handleShareId(record) {
      this.$refs.shareId.open(record);
    },
    //  设置推荐
    handleCommen: function handleCommen(userid, level, set) {
      var arr = "?id=".concat(userid, "&level=").concat(level, "&set=").concat(set);
      var that = this;
      postAction(this.url.commoned + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }

        that.loadData();
      });
    },
    // 设置创客达人
    handleChuanKe: function handleChuanKe(userid) {
      var arr = "?userid=".concat(userid);
      var that = this;
      postAction(this.url.setCreator + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    },
    // 取消创客达人
    excelChuanKe: function excelChuanKe(userid) {
      var arr = "?userid=".concat(userid);
      var that = this;
      postAction(this.url.cancelCreator + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    },
    // 取消v云大使
    excelAmbassador: function excelAmbassador(userid) {
      var arr = "?userid=".concat(userid);
      var that = this;
      postAction(this.url.cancelAambassador + arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    },
    //导出按钮
    exportExcel: function exportExcel() {
      this.$refs.modalForm.add();
      this.$refs.modalForm.title = "导出";
      this.$refs.modalForm.disableSubmit = false;
    } // 导出点击确定按钮
    // const that = this;
    //    getAction(this.url.exportExcel)
    // .then(res => {
    //   if (res.success) {
    //     that.$message.success(res.message);
    //     that.loadData();
    //   }
    // })
    // .catch(err => {
    //   this.$message.warning(err.message);
    //   that.loadData();
    // });
    //  设置市代理
    // handleAgent(record) {
    //   this.$refs.agentModel.open(record);
    // }

  }
};
