import { putAction, postAction, httpAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "ExpressModals",
  data: function data() {
    return {
      enable: 0,
      autoSubmit: false,
      visible: false,
      icon: "",
      model: {},
      percent: "",
      // 图片上传进度
      loading: false,
      title: "新增",
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/mall/company/add",
        edit: "/system/mall/company/update",
        //修改问题分类
        fileUpload: window._CONFIG["domianQiNiu"],
        // 七牛云地址
        qiniuToken: "/qiniuUpToken" // 请求七牛云上传的token

      }
    };
  },
  created: function created() {},
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    makeLength: function makeLength(rule, value, callback) {
      if (value.length > 10) {
        callback("输入字符限制，请重新输入");
      } else {
        callback();
      }
    },
    makephone: function makephone(rule, value, callback) {
      if (value.length > 11) {
        callback("请输入正确得手机号");
      } else {
        callback();
      }
    },
    add: function add() {
      this.icon = "";
      this.percent = "";
      this.confirmLoading = false;
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      if (record && record.id && record.id !== '' && record.id !== undefined) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      this.icon = record.icon;
      this.canimg = record.icon;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;

      if (record.parentId === 0) {
        this.model.parentId = "";
      }

      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, "name", "icon", "phone", "enable"));
      });
    },
    close: function close() {
      this.$emit("close");
      this.percent = "";
      this.icon = "";
      this.visible = false;
    },
    // 图片自定义上传
    imgCustomRequest: function imgCustomRequest(data) {
      var _this2 = this;

      // 开始转圈圈，上传中
      this.loading = true; // 获取文件的后缀名

      var suffix = "." + data.file.name.replace(/^.+\./, ""); //请求token

      httpAction(this.url.qiniuToken, {}, "post").then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this2.url.fileUpload;
          var config = {
            useCdnDomain: true,
            // 域名加速
            region: null // 不指定具体区域，自动识别

          };
          var putExtra = {
            fname: "",
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this2.percent = res.total.percent.toFixed(2);
              _this2.icon = "";
            },
            error: function error() {
              _this2.$message.error("上传失败");

              _this2.loading = false;
              _this2.icon = _this2.canimg;
            },
            complete: function complete(response) {
              var allUrl = "".concat(domain, "/").concat(response.key); // 开始转圈圈，上传中

              _this2.icon = allUrl;
              _this2.loading = false;
            }
          });
        }
      });
    },
    handleOk: function handleOk() {
      var _this3 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var formData = Object.assign(_this3.model, values);
          formData.icon = _this3.icon;

          if (!_this3.model.id) {
            httpurl += _this3.url.add; // let arr = `?name=${formData.name}&icon=${formData.icon}&phone=${formData.phone}`;

            postAction(httpurl, {
              name: formData.name,
              icon: formData.icon,
              phone: formData.phone,
              enable: _this3.enable
            }).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
                that.confirmLoading = false;
                that.close();
              } else {
                that.$message.warning(res.message);
                that.confirmLoading = false;
              }
            }).finally(function () {});
          } else {
            httpurl += _this3.url.edit; // let arr = `?name=${formData.name}&icon=${formData.icon}&phone=${formData.phone}`;

            putAction(httpurl, {
              name: formData.name,
              icon: formData.icon,
              phone: formData.phone,
              enable: _this3.enable,
              id: _this3.model.id
            }).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
              } else {
                that.$message.warning(res.message);
              }
            }).finally(function () {
              that.confirmLoading = false;
              that.close();
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
