import awardCard from "./modules/awardCard";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { deleteAction } from "@/api/manage";
export default {
  name: "awardWinning",
  mixins: [JeecgListMixin],
  components: {
    awardCard: awardCard,
    ProductNewBigImg: ProductNewBigImg
  },
  data: function data() {
    return {
      description: '有奖问答分类页面',
      // 表头
      columns: [{
        title: '名称',
        align: "center",
        dataIndex: 'name'
      }, {
        title: '图标',
        align: "center",
        dataIndex: 'icon',
        scopedSlots: {
          customRender: "header"
        }
      }, {
        title: '排序',
        align: "center",
        dataIndex: 'sort'
      }, {
        title: '用户级别',
        align: "center",
        dataIndex: 'parentId',
        customRender: function customRender(text) {
          if (text === 1) {
            return 'VIP用户';
          } else if (text === 2) {
            return '普通用户';
          }
        }
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: "/system/question/category/list",
        delete: "/system/question/category/delete",
        deleteBatch: "/product/productCategory/deleteBatch",
        exportXlsUrl: "product/productCategory/exportXls",
        importExcelUrl: "product/productCategory/importExcel"
      }
    };
  },
  computed: {
    importExcelUrl: function importExcelUrl() {
      return "".concat(window._CONFIG['domianURL'], "/").concat(this.url.importExcelUrl);
    }
  },
  methods: {
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    handleDelete1: function handleDelete1(id) {
      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }

      var that = this;
      deleteAction(that.url.delete, {
        categoryId: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
