import { postAction } from "@/api/manage";
export default {
  name: "PushMessage",
  data: function data() {
    return {
      form: this.$form.createForm(this),
      content: '',
      //富文本数据
      loading: false,
      conLength: 0,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      validatorRules: {
        content: {
          rules: [{
            required: true,
            message: '请输入推送内容!'
          }, {
            min: 0,
            max: 30,
            message: '长度不超过 30 个字',
            trigger: 'blur'
          }]
        }
      },
      url: {
        json: '/system/message/official/update/json'
      }
    };
  },
  created: function created() {
    this.form.resetFields();
  },
  methods: {
    // 文本框内容变化
    textInputHandle: function textInputHandle() {
      // 获取长度
      var conLength = this.form.getFieldValue('content').length;
      this.conLength = conLength;
      console.log(conLength);
    },
    //重置
    emptyCurrForm: function emptyCurrForm() {
      this.form.resetFields();
      this.content = '';
    },
    // 保存提交
    submitCurrForm: function submitCurrForm() {
      var _this = this;

      this.form.validateFields(['name', 'content'], {
        force: true
      }, function (err, values) {
        if (!err) {
          if (!_this.url.json) {
            return;
          } // if (!this.content || this.content === '' || this.content === undefined) {
          //   this.$message.warning('请输入推送内容！');
          //   return
          // }


          _this.loading = true; // formData.name = values.name;
          // formData.content = values.content;

          var formData = "?name=".concat(values.name, "&content=").concat(values.content);
          console.log(formData);
          postAction(_this.url.json + formData).then(function (res) {
            if (res.success) {
              _this.$message.success(res.message);
            } else {
              _this.$message.warning(res.message);
            }
          }).finally(function () {
            _this.loading = false;
          }); // formData.eventContent = values.eventContent;
        }
      });
    }
  }
};
