import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
export default {
  name: "RechangeModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        money: {
          rules: [{
            required: true,
            message: '请输入支付金额'
          }, {
            validator: this.money
          }]
        },
        trueMoney: {
          rules: [{
            required: true,
            message: '请输入实际获取独家币'
          }, {
            validator: this.trueMoney
          }]
        }
      },
      url: {
        add: "/system/rechange/add",
        edit: "/system/rechange/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    money: function money(rule, value, callback) {
      if (value <= 0) {
        callback('金额数值需大于0');
      } else {
        callback();
      }
    },
    trueMoney: function trueMoney(rule, value, callback) {
      if (value <= 0) {
        callback('独家币金额需大于0');
      } else {
        callback();
      }
    },
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'money', 'trueMoney'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
