import { putAction, postAction, httpAction, getAction } from "@/api/manage";
import pick from "lodash.pick";
import * as qiniu from "qiniu-js";
export default {
  name: "OrderModals",
  data: function data() {
    return {
      courierCompany: '',
      enable: 0,
      autoSubmit: false,
      visible: false,
      icon: "",
      state: '',
      model: {},
      selectList: [],
      percent: "",
      // 图片上传进度
      loading: false,
      title: "新增",
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        edit: "/system/mall/order/update",
        //修改问题分类
        findCompanyName: "/system/mall/company/findCompanyName"
      }
    };
  },
  created: function created() {
    var _this = this;

    getAction(this.url.findCompanyName).then(function (res) {
      console.log(res);

      if (res.success) {
        _this.selectList = res.result;
      } else {
        _this.$message.warning(res.message);
      }
    });
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    makeLength: function makeLength(rule, value, callback) {
      if (value.length > 10) {
        callback("输入字符限制，请重新输入");
      } else {
        callback();
      }
    },
    makephone: function makephone(rule, value, callback) {
      if (value.length > 11) {
        callback("请输入正确得手机号");
      } else {
        callback();
      }
    },
    edit: function edit(record) {
      var _this2 = this;

      this.state = record.state;

      if (record.state == 4) {
        this.autoSubmit = true;
        console.log(this.autoSubmit);
      } else {
        this.autoSubmit = false;
        console.log(this.autoSubmit);
      }

      this.icon = record.icon;
      this.canimg = record.icon;
      this.courierCompany = record.courierCompany;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.parentid = record.parentId == 0 ? record.parentId : undefined;
      this.visible = true;
      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, "courierCompany", "courierNo", "remark"));
      });
    },
    cateoryHandle1: function cateoryHandle1(value) {
      var _this3 = this;

      this.selectList.forEach(function (item) {
        if (item.name === value) {
          // this.groupName1 = item.name
          _this3.courierCompany = item.name;
          console.log("this.courierCompany");
          console.log(_this3.courierCompany);
        }
      });
    },
    close: function close() {
      this.$emit("close");
      this.percent = "";
      this.icon = "";
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this4 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = "";
          var formData = Object.assign(_this4.model, values);
          formData.icon = _this4.icon;
          formData.courierCompany = _this4.courierCompany;
          httpurl += _this4.url.edit; // let arr = `?name=${formData.name}&icon=${formData.icon}&phone=${formData.phone}`;

          putAction(httpurl, {
            courierCompany: formData.courierCompany,
            courierNo: formData.courierNo,
            remark: formData.remark,
            id: _this4.model.id,
            state: _this4.state
          }).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit("ok");
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
