import AvatarModal from "./AvatarModal";
import { getUserId, editUser } from '@/api/api';
export default {
  components: {
    AvatarModal: AvatarModal
  },
  data: function data() {
    return {
      // cropper
      preview: {},
      option: {
        img: '/avatar2.jpg',
        info: true,
        size: 1,
        outputType: 'jpeg',
        canScale: false,
        autoCrop: true,
        // 只有自动截图开启 宽度高度才生效
        autoCropWidth: 180,
        autoCropHeight: 180,
        fixedBox: true,
        // 开启宽度和高度比例
        fixed: true,
        fixedNumber: [1, 1]
      },
      user: {}
    };
  },
  created: function created() {
    var userinfo = this.$store.getters.userInfo;
    this.user = userinfo;
    this.getUser(userinfo.id);
  },
  methods: {
    changeUser: function changeUser() {
      var that = this;
      editUser(this.user).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    // 根据id获取用户信息
    getUser: function getUser(id) {
      var userId = id;
      var that = this;
      getUserId({
        id: userId
      }).then(function (res) {
        if (res.success) {
          that.user = res.result;
        } else {
          console.log(res.message);
        }
      });
    }
  }
};
