import { getAction, httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import JCodeEditor from "@/components/jeecg/JCodeEditor";
import JEditor from "@/components/jeecg/JEditor";
import AvatarModal from "@/views/account/settings/AvatarModal";
import * as qiniu from 'qiniu-js';
export default {
  name: "ProductAuditMoadl",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 17
        }
      },
      labelColc: {
        xs: {
          span: 24
        },
        sm: {
          span: 3
        }
      },
      wrapperColc: {
        xs: {
          span: 24
        },
        sm: {
          span: 20
        }
      },
      labelColk: {
        xs: {
          span: 24
        },
        sm: {
          span: 2
        }
      },
      wrapperColk: {
        xs: {
          span: 24
        },
        sm: {
          span: 21
        }
      },
      loading: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      coverimg: '',
      canimg: '',
      percent: '',
      refusereason: '',
      disableSubmit: false,
      content: '',
      thisType: null,
      headers: {},
      selectList: [],
      validatorRules: {},
      url: {
        getbyId: '/product/productsource/findListByProductId',
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken'
      }
    };
  },
  components: {
    JCodeEditor: JCodeEditor,
    JEditor: JEditor,
    AvatarModal: AvatarModal
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    add: function add() {
      this.coverimg = '';
      this.refusereason = '';
      this.content = '';
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      console.log(record);
      this.coverimg = record.coverimg;
      this.form.resetFields();
      this.getNewModel(record.id);

      if (record.id) {
        this.thisType = record.type;
      }

      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'id', 'title', 'introduction', 'albumname', 'nickname', 'coverimg', 'auditstate', 'catename', 'type', 'price'));
      });
    },
    // 根据id获取具体数据
    getNewModel: function getNewModel(id) {
      var _this2 = this;

      var html = '';
      var that = this;
      getAction(this.url.getbyId, {
        productId: id,
        ischeck: 1
      }).then(function (res) {
        if (res.success) {
          var record = res.result;
          html += '<div style="max-height: 350px;overflow-y: scroll;">';
          record.forEach(function (item) {
            switch (item.type) {
              case 'img':
                html += "<p><img src=\"".concat(item.url, "\" style=\"width:300px;\" /><br/><span style=\"color: #ccc;\">").concat(item.title, "</span></p>");
                break;

              case 'text':
                html += "<p style=\"text-indent: 2em;\"><h3 style=\"text-align: center;\">".concat(item.title, "</h3>").concat(item.url, "</p>");
                break;

              case 'video':
                html += "<p><video controls=\"controls\" width=\"300\" height=\"150\"><source src=\"".concat(item.url, "\" /></video></p>");
                break;

              case 'audio':
                html += "<p><audio src=\"".concat(item.url, "\" controls=\"controls\"></audio></p>");
                break;

              default:
                break;
            }
          });
          html += '</div>';
          _this2.content = html;
          console.log('this.content');
          console.log(_this2.content);
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.coverimg = '';
      this.disableSubmit = false;
      this.content = '';
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 自定义上传
    customRequest: function customRequest(data) {
      var _this3 = this;

      var suffix = "." + data.file.name.replace(/^.+\./, ''); //请求token

      httpAction(this.url.qiniuToken, {}, 'post').then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this3.url.fileUpload;
          var config = {
            useCdnDomain: true,
            region: null
          };
          var putExtra = {
            fname: '',
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this3.loading = true;
              _this3.percent = res.total.percent.toFixed(2);
              _this3.coverimg = '';
            },
            error: function error() {
              _this3.$message.error('上传失败');

              _this3.loading = false;
              _this3.coverimg = _this3.canimg;
            },
            complete: function complete(response) {
              _this3.coverimg = "".concat(domain, "/").concat(response.key);
              _this3.loading = false;
            }
          });
        }
      });
    }
  }
};
